
public class Triangel {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=5;
		for(int i=a;i>=1;i--)
		{
			for(int j=i;j>=1;j--)
			{
				System.out.print("*");
				
			}
			System.out.println();

		}
	System.out.println("-----------------------------------");
	for(int i=1;i<=a;i++)
	{
		for(int j=1;j<=i;j++)
		{
			System.out.print("*");
			
		}
		System.out.println();

}
	}
	
}
