package com.practiceprograms;

import com.programs.String;

public class Basic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Reverse number");
			
		for (int i=0;i<=4;i++)
		{
			System.out.print(i);
		}
		System.out.println();
		for (int i=4;i>=0;i--)
		{
			System.out.print(i);
		}
		// array syntax
		int a[]=new int[10];
		a[0]=10;
		a[1]=9;
		a[2]=8;
		a[3]=7;
		a[4]=6;
		a[5]=5;
		a[6]=4;
		a[7]=3;
		for(int i=0;i<=4;i++)
		{
			System.out.println(a[i]);
		}

		int b[]={1,2,3,4};
		
	}
}
		
		