package com.aaJavaprograms;

public class Star_program {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		for(int i=a;i>=0;i--)
		{
			for(int j=i;j>=0;j--)
			{
				System.out.print("*");
			}
			System.out.println();
		}
		System.out.println("----------------------------------");
		int a1=10;
		for(int i1=0;i1<=a1;i1++)
		{
			for(int j1=0;j1<=i1;j1++)
			{
				System.out.print("*");
			}
				System.out.println();
		}

	}

}
